import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CreateTeamMain(props) {
    const navigate = useNavigate();
    const [project,setProject]  = useState([]);
    const [id,setId] = useState(0);
    const [name,setName] = useState('')
    const [deadline,setDeadline] = useState('')
    const [searchU,setSearchU] = useState([]);
    useEffect(()=>{
        handleReload();
    },[])

    const handleReload = async () =>{
        let res = await axios.get('http://localhost/toDoList_backend/',
            {
                'params':{
                    'createTeamReload':true
                },
                withCredentials:true
            });
        
        const data = res.data
        
        if(data.projects){
            setProject(data.projects);
            handleF(data.projects[0])

            // handleSession(data.projects[0]);
        }
    }


    const  handleF = (data) =>{
        if(deadline.length===0){
            setDeadline(data['deadline'])
        }
        if(name.length===0){
            setName(data['name'])
        }
        if(id===0){
            setId(data['id'])
        }
    }

    const AddProject = async (e) =>{
        e.preventDefault();
        const project_name = document.getElementById('team_name').value
        const project_deadline = document.getElementById('project_deadline').value
        if(project_name.trim()!=='' && project_deadline!==''){
            let data = {
                project_deadline,
                project_name,
                'add_project':true
            }
            let res =await axios.post('http://localhost/toDoList_backend/',data, {withCredentials:true});
            let c = res.data
            if(c.error){
                alert(c.error)
            }
            else if(c.status){
                if(c.status===403){
                    navigate('/');
                }
                else{
                    handleReload()
                }
            }

        }
        
        
    }


    const handleChangeFilter = ()=>{
        
        if(id!==0){
            const data = project.filter(item=>(
                item.id==id
                
            ))
           
            setDeadline(data[0].deadline)
            setName(data[0].name)
        }
       

    }


    const handleSelect = (e)=>{
        setDeadline('')
        setId(e.target.id)
        const r = (project.filter(item=>(
            item.id==e.target.value          
        )))
        setDeadline(r[0]['deadline'])
        setName(r[0]['name'])
        setId(r[0]['id']);
        handleReload()
    }

    const handleDeadlineChange = async(e)=>{
        e.preventDefault()
        let changeProjectDeadline = document.getElementById('changeDeadline').value
        
        if(changeProjectDeadline.length!==0){
            let data = {
                changeProjectDeadline,
                id
            }
            await axios.put('http://localhost/toDoList_backend/',data,{withCredentials:true})
            handleReload()
            handleChangeFilter()
        }
        else{
            alert('empty field')
        } 
    }

    const handleSearch =async(e)=>{
        e.preventDefault();
        let searchUser = document.getElementById('search_user').value
        setSearchU([])
        if(searchUser.trim()!==''){
            let res = await axios.get('http://localhost/toDoList_backend/',{
                'params':{
                    searchUser
                },
                withCredentials: true
                
            });
            let data = res.data
            if(data.searchRes){
                setSearchU(data.searchRes)
            }
        }
       
    }


    const handleAddMember = async(e)=>{
        const id = e.target.getAttribute('data-id');
        let res = await axios.post('http://localhost/toDoList_backend/',{
            'addTeamMemeber':id
        },{withCredentials:true})
    }

    return (
        <div className='container-fluid pb-5 team_cont'>
            <div className='container d-flex justify-content-start align-items-start flex-column gap-4 pt-5'>
                <form onSubmit={AddProject} className='w-100 d-flex justify-content-start align-items-end gap-3 flex-wrap'>
                    <input type='text' id='team_name' placeholder='Project Name' required maxLength={15}/>
                    <div className='d-flex justify-content-between align-items-start gap-3 flex-column'>
                        <label htmlFor='project_deadline'>
                            Project deadline
                        </label>
                    <input type='date' id='project_deadline' required/>
                    </div>
                    <button>
                        <i className='fa fa-plus'></i>
                    </button>
                </form>
                <div className='w-100 pt-4 text-center'>
                    
                    <h2>
                        {name}
                        {/* {sessionStorage.getItem('projectName')} */}
                    </h2>
                    <span>
                        {deadline}
                        {/* {sessionStorage.getItem('projectDeadline')} */}
                    </span>
                </div>
                <div className='w-100 d-flex justify-content-start align-items-end gap-5 flex-wrap'>
                    <div className='d-flex justify-content-between align-items-start gap-3 flex-column'>
                        <label htmlFor='select_team'>
                            Select a project
                        </label>
                        <select id='select_team' onInput={handleSelect}>
                            {project.length!==-1 ? project.map((element)=>(
                                    <option key={element['id']} value={element['id']}>{element['name']}</option>
                                ))
                                    : ''
                            }
                            
                            
                        </select>
                    </div>

                    <form onSubmit={handleSearch} className='d-flex justify-content-start align-items-start gap-3 flex-wrap'>
                        <input type='text' id='search_user' placeholder='Search user' required/>
                        <button type='submit'>
                            <i className='fa fa-search'></i>
                        </button>
                    </form>

                </div>
                <div className='pt-5 w-100 d-flex justify-content-between align-items-start gap-4 flex-wrap'>
                    <div className='team_box p-4'>
                        <h3>Search res</h3>   
                        {searchU.map((element)=>(
                            <div key={element.id} className='other_user_box'>
                                <i className='fa fa-user'></i>
                                <h4>{element.username}</h4>
                                <button data-id={element.id} onClick={handleAddMember}>
                                    <i className='fa fa-plus'></i>
                                </button>
                            </div>
                        ))}                      
                    </div>

                    <div className='team_box p-4'>
                        <h3>Members</h3>
                        <div className='other_user_box'>
                            <i className='fa fa-user'></i>
                            <h4>username_1234</h4>
                            <button>
                                <i className='fa fa-close'></i>
                            </button>
                        </div>  
                    </div>
                    <div className='d-flex justify-content-center align-items-center  flex-wrap gap-4'>
                        <h4>Change deadline</h4>
                        <form onSubmit={handleDeadlineChange} className='d-flex justify-content-center align-items-end gap-3 flex-wrap'>
                            <input type='date' required id='changeDeadline'/>
                            <button type='submit'>
                                Change
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CreateTeamMain;
