import axios from 'axios';
import React, { useEffect, useState } from 'react';

function ProfileProgress(props) {
    const [name,setName] = useState('');
    const [all,setAll] = useState(0);
    const [today,setToday]= useState(0);
    const [graphic,setGrapic] = useState([]);
    useEffect(()=>{
        OnReload();
    },[])



    const OnReload = async ()=>{
        try{
            const res = await axios.get("http://localhost/toDoList_backend/",{
                'params':{
                    'profileReload':true
                },
                withCredentials:true
            });
            const data = res.data
            if(data['status'] && data['status']===200){
                if(!data['profile']){
                    window.location.href = '../index.js';
                    
                }
                else{
                    setName(data['profile'])
                    setAll(data['allCompletedTasks'])
                    setToday(data['todayCompletedTasks'])
                    setGrapic(data['graphic']);

                }
            }

           

        }catch(error){
            console.error(error)
        }
    }
    return (
        <div className='container-fluid'>
            <div className='profile-header container d-flex justify-content-start align-items-center flex-wrap gap-5 pb-5'>
                <h5>{name}</h5>
                <button>Logout</button>
                
            </div>
            <div className='container d-flex justify-content-start align-items-start flex-wrap gap-5'>
                <div className='d-flex justify-content-start align-items-start gap-3 flex-column pe-5'>

                    <div className='doneTasks d-flex justify-content-center gap-2 align-items-center flex-column'>
                        <h4>{today}</h4>
                        <p>Today Completed tasks</p>
                    </div>
                    <div className='doneTasks d-flex justify-content-center gap-2 align-items-center flex-column'>
                        <h4>{all}</h4>
                        <p>All time Completed tasks</p>
                    </div>
                </div>
                <div className='d-flex justify-content-start align-items-start gap-3 flex-column'>
                    <h4>Completed tasks of last 7 days</h4>
                        <div className='d-flex justify-content-start align-items-start gap-3'>
                            {Object.entries(graphic).map(([date,count])=>(
                                <div key={date} className='graphic_candle'>
                                    <p>{count}</p>
                                    <div></div>
                                    <span>{date}</span>
                                </div>
                            ))
                            
                            }
                        </div>
                </div>
            </div>
        </div>
    );
}

export default ProfileProgress;
