                <div className='team_box p-4'>
                    <h3>Tasks</h3>
                </div>
                <div className='team_box p-4'>
                    <h3>Done Tasks  <i className='fa fa-check'></i></h3>
                   
                </div>
            </div>
        </div>
    );
}

export default PendingTasksMain;
