import React, { useEffect, useState } from 'react';

function ProfileProgress(props) {
    const [name,setName] = useState();
    useEffect(()=>{
        OnReload();
    },[])



    const OnReload = async ()=>{
        try{
            const res = await axios.get("http://localhost/toDoList_backend/",{
                'params':{
                    'reload':true
                },
                withCredentials:true
            });
            const data = res.data
            if(data['status'] && data['status']===200){
                if(!data['profile']){
                    document.querySelector('a[href="/auth"]').style.display = 'block'
                    
                }
                else{   
                    setName(data['profile']['other']);
                    document.querySelector('a[href="/profile"]').style.display = 'block'
                    // document.querySelector('a[href="/auth"]').style.display = 'none'
                }
            }

            const body = document.getElementsByTagName('body')[0];
                const check = document.getElementById('themeCheck')
                if(!data['profile']['theme']){
                    body.classList.remove('dark')
                    check.checked = false;
                }
                else{
                    body.classList.add('dark')
                    check.checked = true
                }
           

        }catch(error){
            console.error(error)
        }
    }
    return (
        <div>
            
        </div>
    );
}

export default ProfileProgress;
