import axios from 'axios';
import React from 'react';
import { Link } from 'react-router-dom';

function Email(props) {
    const navigate = useNavigate();
    const handleSuccess = async(c)=>{
        const token = c.credential
        try{
            const data_ =await getFingerPrint();
            data_.googleToken = token
            const res = await axios.post('http://localhost/toDoList_backend/',data_,{withCredentials:true});
            let data = res.data
            if(data['error']){
                alert(data['error'])
            }
            else if(data['status'] && data['status']===200){
                navigate('/profile')
            }

        }catch(error){
            console.error(error)
        }
        
    }
    const getFingerPrint= async()=>{
        const fp =await FingerprintJS.load()
        const res =await fp.get()
        const visitorId = res.visitorId
        const userAgent = navigator.userAgent;
        const info  =parseUserAgent(userAgent);
        const screen= `${window.screen.width}x${window.screen.height}`;
        const data = {
            visitorId,
            screen,
            browser:info.browser,
            os: info.os
            
        }
        return data;
    }

    function parseUserAgent(ua) {
        let browser = "Unknown Browser";
        let os = "Unknown OS";

        if (ua.includes("Windows NT 10")) os = "Windows 10";
        else if (ua.includes("Windows NT 11")) os = "Windows 11";
        else if (ua.includes("Mac OS X")) os = "macOS";
        else if (ua.includes("Android")) os = "Android";
        else if (ua.includes("iPhone") || ua.includes("iPad")) os = "iOS";
        else if (ua.includes("Linux")) os = "Linux";

        if (ua.includes("Chrome/") && !ua.includes("Edg/") && !ua.includes("OPR/")) {
            const match = ua.match(/Chrome\/([\d.]+)/);
            if (match) browser = `Chrome ${match[1]}`;
        } else if (ua.includes("Firefox/")) {
            const match = ua.match(/Firefox\/([\d.]+)/);
            if (match) browser = `Firefox ${match[1]}`;
        } else if (ua.includes("Safari/") && !ua.includes("Chrome/")) {
            const match = ua.match(/Version\/([\d.]+).*Safari/);
            if (match) browser = `Safari ${match[1]}`;
        } else if (ua.includes("Edg/")) {
            const match = ua.match(/Edg\/([\d.]+)/);
            if (match) browser = `Edge ${match[1]}`;
        } else if (ua.includes("OPR/")) {
            const match = ua.match(/OPR\/([\d.]+)/);
            if (match) browser = `Opera ${match[1]}`;
        }

  return { browser, os };
}


    const handleSubmit = async(e)=>{
        e.preventDefault()

        const email = document.getElementById('email').value
        const password = document.getElementById('password').value;
        if(
            (email.length!==0 && email.trim()!=='')
            && (password.length!==0 && password.trim()!=='')
        ){
            try{
                const data ={};
                data.email = email
                data.password = password

                const res = await axios.post("http://localhost/toDoList_backend/", data,{withCredentials:true});
                const content = res.data
                if(content['error']){
                    alert(content['error'])
                }
                else if(content['status'] && content['status']===200){
                    navigate('/profile')
                }
            }catch(error){
                console.error(error)
            }
        }
        else{
            alert('empty field')
        }
        
    }


    return (
        
        <div className='container-fluid pt-5 auth-cont'>
            <div className='pt-5 container d-flex justify-content-center align-items-center'>
                <div className='d-flex justify-content-center align-items-center flex-column gap-4 auth-div'>
                    <h3>Forgot Password</h3>
                    <form onSubmit={handleSubmit} className='d-flex justify-content-center align-items-center flex-column gap-4'>
                        <input id='email' type='email' placeholder='email' required/>
                        <button type='submit'>Next!</button>
                    </form>
                    <Link to={'/signUp'} style={{color: 'white',display:'flex'}}>Don't have an account ?</Link>
                </div>
                
            </div>
        </div>
            
    );
}

export default Email;
