import React, { useEffect, useState } from 'react';
import axios from 'axios';
function CategoriesMain(props) {
    const [data,setData] = useState([]);
    useEffect(()=>{
        getInfo();
    },[])

    const getInfo = async ()=>{
        try{
            const res = await axios.get('http://localhost/toDoList_backend/' ,{
                'params':{
                    'categories':true
                },
                withCredentials:true
            })
            console.error(res.data)
            setData(res.data);
        }
        catch(error){
            console.error(error)
        }
    }


    const handleDelete = async(e)=>{
        try{
            const res = await axios.delete('http://localhost/toDoList_backend/',{
                'data':{
                    'categorie':true,
                    'id':e.target.getAttribute('data-id')
                }
            });
            const data =res.data
            if(data['error']){
                alert(data['error'])
            }
            else{
                getInfo();
            }
        }catch(error){
            console.error(error)
        }
        console.error(e.target.getAttribute('data-id'))
    }

    const handleSubmit = async(e)=>{
        e.preventDefault()

        try{
            const category = document.getElementById('category_input').value;
            const res = await axios.post('http://localhost/toDoList_backend/',{
                'addCategory':category
            });
            const data =  res.data;
            console.error(data)
            // if(data['error']){
            //     alert(data['error'])
            // }
            // else{
            //     getInfo();
            // }

        }catch(error){
            console.error(error)
        }
    }
    return (
        <div className='container-fluid  py-5'>
            <form onSubmit={handleSubmit} className='container add_category d-flex justify-content-start align-items-start flex-wrap gap-4 pb-5'>
                <input type='text' maxLength={20} id='category_input' placeholder='Add a category'/>
                <button type='submit'>Add</button>
            </form>
            <div className='container d-flex justify-content-start align-items-start gap-4 flex-wrap'>
                {data.map((element) => (
                    <div  key={element['id']} className='categorie_box d-flex justify-content-start align-items-center flex-column'>
                        <div className='w-100 text-end'>
                            <i className="fa fa-trash delete" aria-hidden="true" data-id={element['id']} onClick={handleDelete}></i>
                        </div>
                        <h2>{element['categorie']}</h2>
                    </div>
                    // console.error(element)
                ))}
            </div>
            
            
        </div>
    );
}

export default CategoriesMain;
