import React from 'react';

function HomeMain(props) {
    const handleMoreOpt = (e)=>{
        let count = parseInt(e.target.getAttribute('data-count'))
        const key = e.target.getAttribute('data-key')
        const open = document.querySelector(`div[data-opt="${key}"]`)
        if(count%2===1){
            open.classList.add('task_opt_open')
        }
        else{
            open.classList.remove('task_opt_open')
        }
        count +=1;
        e.target.setAttribute('data-count',count)        
    }

    const handleFlag = (e)=>{
        let count = parseInt(e.target.getAttribute('data-count'))
        const key = e.target.getAttribute('data-key')
        if(count===1){
            e.target.stlyle.color= 'red';
        }
        else if(count===2){
            open.classList.add('red_flag')
        }
        else{
            open.classList.remove('task_opt_open')
        }
        count +=1;
        e.target.setAttribute('data-count',count)        
    }


    return (
        <div className='container-fluid pt-4 pt-md-5'>
            <div className='container d-flex justify-content-start align-items-start flex-column gap-5'>
                <form className='addForm pb-4'>
                    <input type='text' minLength={1} maxLength={40} placeholder='Add a task'/>
                    <button type='submit'>Add</button>
                </form>

                <ul className='d-flex justify-content-start align-items-start flex-wrap gap-4'>
                    <li>
                        <div className='task_div'>
                            <input type='checkbox'/>
                            <h3>Task</h3>
                            
                            <div data-opt='1' className='d-flex justify-content-start gap-3 align-items-center task_opt'>
                                <i className='fa fa-ellipsis-h task-moreOpt' data-count='1' data-key='1' onClick={handleMoreOpt}></i>
                                <i className="fa fa-trash" aria-hidden="true"></i>
                                <i className="fa fa-flag" onClick={handleFlag} data-count='1'  data-key={1} aria-hidden="true"></i>
                            </div>
                        </div>
                        
                    </li>

                    <li>
                        <div className='task_div'>
                            <input type='checkbox'/>
                            <h3>Task</h3>
                            <div data-opt='2' className='d-flex justify-content-start gap-3 align-items-center task_opt'>
                                <i className='fa fa-ellipsis-h task-moreOpt' data-count='1' data-open='1' data-key='2' onClick={handleMoreOpt}></i>
                                <i className="fa fa-trash" aria-hidden="true"></i>
                                <i className="fa fa-flag" onClick={handleFlag} data-count='1' data-key={2} aria-hidden="true"></i>
                            </div>
                        </div>
                    </li>
                </ul>
            </div>
        </div>
    );
}

export default HomeMain;
