import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import FingerprintJS from '@fingerprintjs/fingerprintjs';
import { useNavigate } from 'react-router-dom';
import '../assets/css/auth.css';
function Auth(props) {
    const navigate = useNavigate();
    const handleSuccess = async(c)=>{
        const token = c.credential
        try{
            const res = await axios.post('http://localhost/toDoList_backend/',{googleToken:token},{withCredentials:true});
            let data = res.data
            if(data['error']){
                alert(data['error'])
            }
            else if(data['status'] && data['status']===200){
                navigate('/profile')
            }

        }catch(error){
            console.error(error)
        }
        
    }

    const handleError =()=>{
        alert('Login failed')
    }

    const handleSubmit = async(e)=>{
        e.preventDefault()

        const username = document.getElementById('username').value
        const password = document.getElementById('password').value;
        if(
            (username.length!==0 && username.trim()!=='')
            && (password.length!==0 && password.trim()!=='')
        ){
            try{
                const data = {
                    username,
                    password,
                    
                }
                const res = await axios.post("http://localhost/toDoList_backend/", data,{withCredentials:true});
                const content = res.data
                if(content['error']){
                    alert(content['error'])
                }
                else if(content['status'] && content['status']===200){
                    navigate('/profile')
                }
            }catch(error){
                console.error(error)
            }
        }
        else{
            alert('empty field')
        }
        
    }


    return (
        
        <div className='container-fluid pt-5 auth-cont'>
            <div className='pt-5 container d-flex justify-content-center align-items-center'>
                <div className='d-flex justify-content-center align-items-center flex-column gap-4 auth-div'>
                    <h3>Authentication</h3>
                    <GoogleLogin

                        onSuccess={handleSuccess}
                        onError={handleError} 
                    />
                    <span>or</span>
                    <form onSubmit={handleSubmit} className='d-flex justify-content-center align-items-center flex-column gap-4'>
                        <input id='username' type='text' placeholder='username' required maxLength={13}/>
                        <input id='password' type='password' placeholder='password' required/>
                        <button type='submit'>GO!</button>
                    </form>
                </div>
                
            </div>
        </div>
    );
}

export default Auth;
