import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

function LinkPages(props) {
    const [count ,setCount] = useState(1);
    function handleSideBar(){
        const sideBar = document.querySelector('.sideBar')
        setCount(count+1)
        // console.error(sideBar)
        if(count%2===1){
            sideBar.classList.add('sideBarOpen')
        }
        else{
            sideBar.classList.remove('sideBarOpen')
        }
    }


    return (
        <div className='container-fluid py-4 py-md-5  navbar-cont-fl'>
            <div className='container d-flex justify-content-between align-items-center navbar-cont'>
                <h2>To-Do-List <i className="fa fa-list-ul" aria-hidden="true"></i></h2>
                <i className='fa fa-bars' onClick={handleSideBar}></i>
                <NavLink to={'/profile'}>
                    <i className='fa fa-user'></i>
                </NavLink>
            </div>
            <div className='sideBar p-5'>
                <h2>To-Do-List <i className="fa fa-list-ul" aria-hidden="true"></i></h2>
                <ul className='pt-5 d-flex justify-content-start align-items-start gap-4 flex-column'>
                    <li>
                        <NavLink to={'/'}>
                            Home
                            <i className='fa fa-home'></i>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to={'/habits'}>
                            Habits
                            <i className='fa fa-repeat'></i>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to={'/categories'}>
                            Categories
                            <i className='fa fa-th'></i>
                        </NavLink>
                    </li>
                    <li>
                        Theme
                        <input type='checkbox'/>
                    </li>
                </ul>
            </div>
        </div>
    );
}

export default LinkPages;
