import axios from 'axios';
import React, { useEffect, useState } from 'react';

function TeamTasksMain(props) {
    const [projects,setProjects] = useState([]);
    const [minDate, setMinDate] = useState(0);
    const [members, setMembers] = useState([])
    const [name, setName] = useState('');
    const [id,setId] = useState(0);
    useEffect(()=>{
        handleReload()
        const today = new Date().toISOString().split("T")[0]
        setMinDate(today)
        projectSelectorChecker();
    },[])


    const handleReload = async()=>{
        let res = await axios.get('http://localhost/toDoList_backend/',{
            'params':{
                'teamTasksReload':true
            },
            withCredentials:true
        });
        const data = res.data
        if(data.projects){
            setProjects(data.projects)
        }
        console.error(data)
    }

    const handleProjectSelect = async(e)=>{
        const el = e.target.selectedOptions[0]
        const name1 = el.getAttribute('data-name')
        setName(name1)
        const res =  await axios.get('http://localhost/toDoList_backend/',{
            'params':{"getTeamMembers":el.value},
            withCredentials:true
        });
        const data = res.data
        if(data.members){
            setMembers(data.members)
        }
    }

    const handleAddTask = async(e)=>{
        e.preventDefault()
        const task = e.target.elements.task.value
        const deadline = e.target.elements.task_deadline.value
        const select = e.target.elements.select_member
        const option = select.selectedOptions[0]
        if(task.trim()===''){
            alert('task is empty')
        }
        if(option.disabled){
            alert('Choose a member!')
        }
        
        if(deadline.length!==0 && task.trim!=='' && !option.disabled){
            const data = {
                task,
                deadline,
                'member':option.value
            }
            console.error(data)
            const res = await axios.post('http://localhost/toDoList_backend/',
                data,{withCredentials:true})
            
        }   
        
    }

    return (
        <div className='container-fluid pb-5 team_cont'>
            <div className='container d-flex justify-content-start align-items-start flex-column gap-4 pt-5'>
                <h2 className='w-100 text-center'>{name}</h2>
                <div className='w-100 d-flex justify-content-between align-items-end gap-4 flex-wrap pt-4'>
                    <div className='d-flex justify-content-between align-items-start gap-3 flex-column'>
                        <select id='select_team' onInput={handleProjectSelect}>
                            <option selected disabled>Select a project</option>
                            {projects.map(item=>(
                                <option key={item.id} value={item.id} data-name={item.name}>{item.name}</option>
                            ))}

                        </select>
                    </div>

                    <form onSubmit={handleAddTask} className='d-flex justify-content-start align-items-end gap-4 flex-wrap'>
                        <input required type='text' name='task' placeholder='Write a task'/>
                        <div className='d-flex justify-content-between align-items-start gap-3 flex-column'>
                            <label htmlFor='task_deadline'>
                                Task deadline
                            </label>
                            <input type='date' name='task_deadline' required min={minDate} onKeyDown={(e)=>e.preventDefault()}/>
                        </div>

                        <div className='d-flex justify-content-between align-items-start gap-3 flex-column'>
                            <select name='select_member'>
                                <option selected disabled>Select a member</option>
                                {members.map(item=>(
                                    <option key={item.id} value={item.id}>{item.username}</option>
                                ))}
                            <option value='all'>All</option>
                            </select>
                        </div>

                        <button type='submit'>
                            <i className='fa fa-plus'></i>
                        </button>
                    </form>
                </div>

                <div className='pt-5 d-flex justify-content-start align-items-center gap-3'>
                    <input id='task_filter' type='checkbox'/>
                    <label htmlFor='task_filter'>My Tasks</label>
                </div>

                <div className='pt-3 w-100 d-flex justify-content-between align-items-start gap-4 flex-wrap'>
                    <div className='team_box p-4'>
                        <h3>Tasks </h3>    
                        <div className='team-tasks d-flex w-100 justify-content-between aplign-items-center gap-4 flex-wrap'>
                            <div className='d-flex justify-content-center align-items-center  flex-wrap gap-3'>
                                <i className='fa fa-user'></i>
                                <h4>username_1234</h4>
                            </div>
                           <i className='fa fa-trash'></i>
                           <div className='ps-4 w-100 d-flex justify-content-between align-items-center gap-4 flex-wrap'>
                                <p className='text_task'>task</p>
                                <span>deadline</span>

                           </div>

                            {/* <h3 data-key={task['id']} className='text_task'  onBlur={handleBlur}>{task['task']}</h3>
                                
                                <div data-opt={task['id']} className='task_opt'>
                                    <i className='fa fa-ellipsis-h task-moreOpt' data-count='1' data-key={task['id']} onClick={handleMoreOpt}></i>
                                    <i className="fa fa-trash" aria-hidden="true" data-key={task['id']} onClick={handleDelete}></i>
                                    <i className="fa fa-pencil-square-o" onClick={handleEdit} data-key={task['id']} data-edit='0' aria-hidden="true"></i>
                                </div> */}
                        </div>        
                        <div className='team-tasks d-flex w-100 justify-content-between aplign-items-center gap-3 flex-wrap'>
                            <div className='d-flex justify-content-center align-items-center  flex-wrap gap-3'>
                                <i className='fa fa-user'></i>
                                <h4>username_1234</h4>
                            </div>

                            <p className='text_task'>task</p>
                           <i className='fa fa-trash'></i>
                           <span className='w-100 text-end'>deadline</span>
                        </div>              
                    </div>

                    <div className='team_box p-4'>
                        <h3>Done <i className='fa fa-check'></i></h3>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default TeamTasksMain;
