import React, { useEffect, useState } from 'react';
import axios from 'axios'
function HomeMain(props) {
    const [data,setData] = useState([]);
    const [category,setCategory] = useState('');
    useEffect(()=>{
        handleReload();
    },[])

    const handleReload = async()=>{
        try{
            const res =  await axios.get('http://localhost/toDoList_backend/',{
                'params':{
                    'homeReload':true
                },
                withCredentials:true
                
            })
           
            if(res.data['categories']){
                let c=   res.data['categories']
                setData(c)
                if(category.length===0){
                    setCategory(c[0]['categorie'])
                }
            }
            
        }catch(error){
            console.error(error)
        }
    }

    const handleMoreOpt = (e)=>{
        let count = parseInt(e.target.getAttribute('data-count'))
        const key = e.target.getAttribute('data-key')
        const open = document.querySelector(`div[data-opt="${key}"]`)
        if(count%2===1){
            open.classList.add('task_opt_open')
        }
        else{
            open.classList.remove('task_opt_open')
        }
        count +=1;
        e.target.setAttribute('data-count',count)        
    }

    const handleFlag = (e)=>{
        let count = parseInt(e.target.getAttribute('data-count'))
        count +=1;
        if(count===1){
            e.target.style.color= 'red';
        }
        else if(count===2){
            e.target.style.color= 'orange';
        }
        else if(count===3){
            e.target.style.color= 'green';
            count = -1;
        }
        else{
            e.target.style.color= 'white';
        }
        
        e.target.setAttribute('data-count',count)        
    }


    const handleEdit = (e)=>{
        let edit= e.target.getAttribute('data-edit')
        let key = e.target.getAttribute('data-key')
        let text = document.querySelector(`.text_task[data-key='${key}']`)
        if(edit==='1'){
            text.removeAttribute('contentEditable')
            
            e.target.setAttribute('data-edit','0')
            e.target.style.color = 'white'
        }
        else{
            text.setAttribute('contentEditable',true)
            e.target.setAttribute('data-edit','1')
            e.target.style.color = 'rgb(34, 56, 67)'
        }
        console.error(text)
    }


    const handleSubmit = async (e)=>{
        e.preventDefault()
        try{
           
            const task = e.target.querySelector('input').value
            const res = await axios.post('http://localhost/toDoList_backend/',
                {
                    'addTask':true,
                    task,
                    category
                },
            {withCredentials: true})
        }catch(error){
            console.error(error)
        }
    }


    const handleSelect = (e)=>{
        setCategory(e.target.value)
    }
    return (
        <div className='container-fluid pt-4 pt-md-5'>
            <div className='container d-flex justify-content-start align-items-start gap-5 flex-wrap'>
                <select onInput={handleSelect}>
                    {data.map((element)=>(
                        <option  key={element['categorie']} value={element['categorie']}>{element['categorie']}</option>
                    ))}  
                </select>



                <form className='addForm pb-4' onSubmit={handleSubmit}>
                    <input type='text' minLength={1} maxLength={20} placeholder='Add a task'/>
                    <button type='submit'>
                        <i className='fa fa-plus'></i>
                    </button>
                </form>

                
                
            </div>
            <h3 className='w-100 text-center py-3 category_h3'>category</h3>
            <div className='container d-flex justify-content-between align-items-start flex-wrap gap-2 pt-3'>
                <ul className='d-flex justify-content-start align-items-start flex-wrap gap-3 tasks'>
                    <li>
                        <h3 className='w-100 text-center task_header'>Tasks</h3>
                    </li>
                    <li>
                        <div className='task_div'>
                            <input type='checkbox'/>
                            <h3 data-key={1} className='text_task'>Taskdfkl;kiuytrsdcvbnhjyui7utrhngrtufdbdhrey</h3>
                            
                            <div data-opt='1' className='task_opt'>
                                <i className='fa fa-ellipsis-h task-moreOpt' data-count='1' data-key='1' onClick={handleMoreOpt}></i>
                                <i className="fa fa-trash" aria-hidden="true"></i>
                                <i className="fa fa-pencil-square-o" onClick={handleEdit} data-key='1' data-edit='0' aria-hidden="true"></i>
                                <i className="fa fa-flag" onClick={handleFlag} data-count='0'  data-key={1} aria-hidden="true"></i>
                            </div>
                        </div>
                        
                    </li>

                    <li>
                        <div className='task_div'>
                            <input type='checkbox'/>
                            <h3 data-key={2} className='text_task'>Task</h3>
                            <div data-opt='2' className='task_opt'>
                                <i className='fa fa-ellipsis-h task-moreOpt' data-count='1' data-open='1' data-key='2' onClick={handleMoreOpt}></i>
                                <i className="fa fa-trash" aria-hidden="true"></i>
                                <i className="fa fa-pencil-square-o" onClick={handleEdit} data-key='2'  data-edit='0' aria-hidden="true"></i>
                                <i className="fa fa-flag" onClick={handleFlag} data-count='0' data-key={2} aria-hidden="true"></i>
                            </div>
                        </div>
                    </li>
                    <li>
                        <div className='task_div'>
                            <input type='checkbox'/>
