import axios from 'axios';
import React, { useEffect, useState } from 'react';

function ProfileProgress(props) {
    const [name,setName] = useState();
    useEffect(()=>{
        OnReload();
    },[])



    const OnReload = async ()=>{
        try{
            const res = await axios.get("http://localhost/toDoList_backend/",{
                'params':{
                    'profileReload':true
                },
                withCredentials:true
            });
            const data = res.data
            if(data['status'] && data['status']===200){
                if(!data['profile']){
                    document.querySelector('a[href="/auth"]').style.display = 'block'
                    
                }
                else{
                    setName(data['profile'])
                }
            }

           

        }catch(error){
            console.error(error)
        }
    }
    return (
        <div className='container-fluid'>
            <div className='container d-flex justify-content-start align-items-center flex-wrap gap-4 pb-4'>
                <h4>{name}</h4>
            </div>
        </div>
    );
}

export default ProfileProgress;
