import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';

function LinkPages(props) {
    const [count ,setCount] = useState(1);

    useEffect(()=>{
        OnReload();
    },[])

    const OnReload = async ()=>{
        try{
            const res = await axios.get("http://localhost/toDoList_backend/",{
                'params':{
                    'reload':true
                },
                withCredentials:true
            });
            const data = res.data
            if(data['status'] && data['status']===200){
                if(!data['profile']){
                    document.getElementById('to_prof').style.display = 'none'
                }
                else{   
                    document.getElementById('to_log').style.display = 'block'
                }
            }
           

        }catch(error){
            console.error(error)
        }
    }

    const handleSideBar =()=>{
        const sideBar = document.querySelector('.sideBar')
        setCount(count+1)
        if(count%2===1){
            sideBar.classList.add('sideBarOpen')
        }
        else{
            sideBar.classList.remove('sideBarOpen')
        }
    }

    const handleCheck = (e) =>{
        const body = document.getElementsByTagName('body')[0];
        if(e.target.checked){
            body.classList.add('dark')
        }   
        else{
            body.classList.remove('dark')
        }
    }


    return (
        <div className='container-fluid py-4 py-md-5  navbar-cont-fl'>
            
            <div className='container d-flex justify-content-between align-items-center navbar-cont'>
                <h2>To-Do-List <i className="fa fa-list-ul" aria-hidden="true"></i></h2>
                <i className='fa fa-bars' onClick={handleSideBar}></i>
                <NavLink to={'/profile'} id='to_prof'>
                    <i className='fa fa-user'></i>
                </NavLink>
                <NavLink to={'/auth'} id='to_log'>
                    <button>Account Access</button>
                </NavLink>
            </div>
            <div className='sideBar p-5'>
                <i className='fa fa-close w-100 text-end pb-3' onClick={handleSideBar}></i>
                <h2>To-Do-List <i className="fa fa-list-ul" aria-hidden="true"></i></h2>
                <ul className='pt-5 d-flex justify-content-start align-items-start gap-3 flex-column'>
                    <li>
                        <NavLink to={'/'}>
                            Home
                            <i className='fa fa-home'></i>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to={'/habits'}>
                            Habits
                            <i className='fa fa-repeat'></i>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to={'/categories'}>
                            Categories
                            <i className='fa fa-th'></i>
                        </NavLink>
                    </li>
                    <li>
                        Theme
                        <input type='checkbox' onClick={handleCheck}/>
                    </li>
                </ul>
            </div>
        </div>
    );
}

export default LinkPages;
